def handleCalc2():
    sender_address = web3.toChecksumAddress(textEdit1.toPlainText())   
    balance = web3.eth.get_balance(sender_address)
    humanReadable = web3.fromWei(balance, 'ether')
    rz.append('读取到钱包余额：' + str(humanReadable) + 'bnb')
    rz.append('连接bsc主网结果：' + str(web3.isConnected()) + '。')


def handleCalc():
    
    sender_address = web3.toChecksumAddress(textEdit1.toPlainText())
    private_key = textEdit2.toPlainText()
    gas_price = float(textEdit3.toPlainText())
    usdt_wantbuy = float(textEdit4.toPlainText())
    tokenToBuy = web3.toChecksumAddress(textEdit5.toPlainText())
    nonce = web3.eth.get_transaction_count(sender_address)
    usdt_wei = web3.toWei(usdt_wantbuy,'ether')
    
    pancakeswap2_txn_bnbtotoken = contract.functions.swapExactETHForTokens(
 	1, [wbnb,tokenToBuy], sender_address, (int(time.time()) + 10000)
).buildTransaction({
 	'from': sender_address,
 	'chainId': 56,
 	'nonce': nonce,
 	'gas': 320000,
 	'value': web3.toWei(usdt_wantbuy, 'ether'),
 	'gasPrice': web3.eth.gasPrice,
})
    pancakeswap2_txn_bnbtousdttotoken = contract.functions.swapExactETHForTokens(
 	1, [wbnb,usdt,tokenToBuy], sender_address, (int(time.time()) + 10000)
).buildTransaction({
 	'from': sender_address,
 	'chainId': 56,
 	'nonce': nonce + 1,
 	'gas': 340000,
 	'value': web3.toWei(usdt_wantbuy, 'ether'),
 	'gasPrice': web3.eth.gasPrice,
})
    pancakeswap2_txn_bnbtobusdtotoken = contract.functions.swapExactETHForTokens(
 	1, [wbnb,busd,tokenToBuy], sender_address, (int(time.time()) + 10000)
).buildTransaction({
 	'from': sender_address,
 	'chainId': 56,
 	'nonce': nonce + 2,
 	'gas': 360000,
 	'value': web3.toWei(usdt_wantbuy, 'ether'),
 	'gasPrice': web3.eth.gasPrice,
})
    
    signed_txn_wbnb = web3.eth.account.sign_transaction(pancakeswap2_txn_bnbtotoken, private_key)
    signed_txn_usdt = web3.eth.account.sign_transaction(pancakeswap2_txn_bnbtousdttotoken, private_key)
    signed_txn_busd = web3.eth.account.sign_transaction(pancakeswap2_txn_bnbtobusdtotoken, private_key)
    tx_token_wbnb = web3.eth.send_raw_transaction(signed_txn_wbnb.rawTransaction)
    tx_token_usdt = web3.eth.send_raw_transaction(signed_txn_usdt.rawTransaction)
    tx_token_busd = web3.eth.send_raw_transaction(signed_txn_busd.rawTransaction)
    
    rz.append('交易哈希():'+web3.toHex(tx_token_wbnb))
    rz.append('交易哈希():'+web3.toHex(tx_token_usdt))
    rz.append('交易哈希():'+web3.toHex(tx_token_busd))
    
    


